mapper.select:
SELECT SEND_NO, INTEGRATION_MESSAGE_NO, ( SELECT GRADE FROM STUDENT WHERE STUDENT.STUDENT_NO = SEND.STUDENT_NO )GRADE, ( SELECT ADMINISTRATOR_NAME FROM Administrator WHERE ADMINISTRATOR.ADMINISTRATOR_NO = SEND.ADMINISTRATOR_NO )ADMINISTRATOR_NAME, ( SELECT STUDENT_NAME FROM STUDENT WHERE STUDENT.STUDENT_NO = SEND.STUDENT_NO )STUDENT_NAME, ( SELECT TITLE FROM Integration_Message WHERE INTEGRATION_MESSAGE.INTEGRATION_MESSAGE_NO = SEND.INTEGRATION_MESSAGE_NO )TITLE, ( SELECT CONTENT FROM Integration_Message WHERE INTEGRATION_MESSAGE.INTEGRATION_MESSAGE_NO = SEND.INTEGRATION_MESSAGE_NO )CONTENT, TO_CHAR(SEND_DATE, 'YYYY-MM-DD HH:MM') AS SEND_DATE, SEND_STATUS, SEND_STATUS FROM SEND
AND ADMINISTRATOR_NO = #{administratorNo}
AND STUDENT_NO = #{studentNo}
ORDER BY SEND_NO DESC
